import React from 'react';
import './header.css'

import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { makeStyles } from "@material-ui/core/styles";
import {RangeStepInput} from 'react-range-step-input';

// Function to update the size property
function onSizeChange(e) {
    const newVal = e.target.value;
    this.setState({size: newVal}, () => {this.generateNewArray();});
}

    // Function to update the speed property
function onSpeedChange(e) {
    const newVal = e.target.value;
    this.setState({speed: newVal});
}

    // Generate new array to sort
function generateNewArray() {
    var new_array = [];
    for (var i = 0; i < this.state.size; i++) {
        var height = this.getRandomInt();
        new_array.push(<GridElement className="grid-element" key={i} element_height={height} style={{backgroundColor: "#282c34"}} />);
    }
    this.setState({array: new_array});
    this.setState({starting_array: new_array});

}

function Header({speed, size}) {
    return(
        <header className="App-header">
        <span className="header-title">
            Sorting Visualizer
        </span>
        <div className="sliders">
            <span className="slider-div">
                <div className="slider-title"> Size</div>
                <div> <RangeStepInput 
                min={4} max={50}
                value={size} step={1}
                onChange={onSizeChange.bind(this)}
                /> </div>
            </span>
            <span className="slider-div">
                <div className="slider-title"> Speed</div>
                <div> <RangeStepInput 
                min={5} max={500}
                value={speed} step={1}
                onChange={onSpeedChange.bind(this)}
                /> </div>
            </span>
        </div>
        <div className="header-buttons">
            <div className="header-button">
                <button className="sort-button" onClick={generateNewArray.bind(this)}> New Array </button>
            </div>
            <div className="header-button"> 
                <button className="sort-button" onClick={this.sortElements.bind(this)}> Sort! </button>
            </div>
        </div>
        <span className="header-element">
        <FormControl variant="standard" className="algorithm-select">
            <InputLabel variant="standard" sx={{ m: 1, minWidth: 150 }}></InputLabel>
            <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={this.state.algorithm}
                onChange={this.updateAlgorithm}
                label="Age"
                disableUnderline
            >
            <MenuItem value={"Bubble Sort"}>Bubble Sort</MenuItem>
            <MenuItem value={"Quick Sort"}>Quick Sort</MenuItem>
            <MenuItem value={"Heap Sort"}>Heap Sort</MenuItem>
            </Select>
        </FormControl>
        </span>
        </header>
 );

}

export default Header;
