import React from 'react';
import './header.css'

import TextField from "@material-ui/core/TextField";
import MenuItem from '@mui/material/MenuItem';
import { makeStyles } from "@material-ui/core/styles";

// Function to update the size property

const styles = makeStyles({
    root: {
        width: 200,
        "& .MuiOutlinedInput-input": {
          color: "white"
        },
        "& .MuiInputLabel-root": {
          color: "white"
        },
        "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
          borderColor: "white"
        },
        "&:hover .MuiOutlinedInput-input": {
          color: "white"
        },
        "&:hover .MuiInputLabel-root": {
          color: "white"
        },
        "&:hover .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
          borderColor: "white"
        },
        "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-input": {
          color: "white"
        },
        "& .MuiInputLabel-root.Mui-focused": {
          color: "white"
        },
        "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline": {
          borderColor: "white"
        }
    },
    icon: {
      color: "white"
    }
  });

function Header(props) {
    const classes = styles();
    return(
        <header className="App-header">
            <div className="header-title">
                Sorting Visualizer
            </div>
            <TextField
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={props.algorithm}
                // onChange={props.updateSelectedAlgorithm}
                label="Visualization Type"
                className={classes.root}
                variant="outlined"
                select
                SelectProps={{classes: {icon: classes.icon}}}
            >
            <MenuItem value={"Bar Height"}>Bar Height</MenuItem>
            <MenuItem value={"Color"}>Color</MenuItem>
            </TextField>

            <TextField
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={props.algorithm}
                onChange={props.updateSelectedAlgorithm}
                label="Sorting Algorithm"
                className={classes.root}
                variant="outlined"
                select
                SelectProps={{classes: {icon: classes.icon}}}
                >
            <MenuItem value={"Bubble Sort"}>Bubble Sort</MenuItem>
            <MenuItem value={"Quick Sort"}>Quick Sort</MenuItem>
            <MenuItem value={"Heap Sort"}>Heap Sort</MenuItem>
            </TextField>

                <div className="header-button">
                    <button className="sort-button" onClick={props.generateNewArray}> New Array </button>
                </div>
                <div className="header-button"> 
                    <button className="sort-button" onClick={props.sortElements}> Sort! </button>
                </div>
        </header>
 );

}

export default Header;
