function getBubbleSortAnimations(array) {
    // console.log("Start of BUBBLE SORT", this.state.array);
    // this.printHeights(this.state.array);
    var animations = [];
    for (let i = 0; i < array.length; i++) {
        for (let j = 0; j < array.length - i - 1; j++) {
            // animations.push([j, j+1]);
            animations.push(["check", j, j+1]);
            // animations.push(["uncheck", j, j+1]);
            // this.printHeights(this.state.array);
            var heightOne = array[j].props.element_height;
            var heightTwo = array[j+1].props.element_height;
            if ( heightOne > heightTwo ) {
                animations.push(["swap-wrong", j, j+1]);
                animations.push(["swap-right", j, j+1]);
                var temp = array[j]
                array[j] = array[j+1];
                array[j+1] = temp;
            } else {
                animations.push(["uncheck", j, j+1]);
            }
        }
    }
    // console.log("End of BUBBLE SORT", this.state.array);
    // this.printHeights(this.state.array);
    return animations;
}

export default getBubbleSortAnimations;
