import SortingGrid from './SortingGrid'
import {RangeStepInput} from 'react-range-step-input';
import React from 'react';
import GridElement from './GridElement';

import './App.css';

function myBubbleSort(array) {
    var animations = [];
    for (let i = 0; i < array.length; i++) {
        for (let j = 0; j < array.length - i - 1; j++) {
            animations.push([j, j+1]);
            if( array[j].props.element_height > array[j + 1].props.element_height ) {
                animations.push([j, j+1]);
                var temp = array[j]
                array[j] = array[j+1];
                array[j+1] = temp;
            }
        }
    }
    return [array, animations];
}


function bubbleSort(arr) {
    var i, j;
    var len = arr.length;

    var isSwapped = false;

    for(i =0; i < len; i++){

      isSwapped = false;

      for(j = 0; j < len; j++){ 
          if (arr[j+1] === undefined) {
              continue;
          }
          if(arr[j].props.element_height > arr[j + 1].props.element_height){
            var temp = arr[j]
            arr[j] = arr[j+1];
            arr[j+1] = temp;
            isSwapped = true;
          }
      }

      // IF no two elements were swapped by inner loop, then break 

      if(!isSwapped){
        break;
      }
    }

    // Print the array
    return(arr);
}

class SortingVisualizer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          array: [],
          slider: 50,
        }
        // this.generateNewArray = this.generateNewArray.bind(this);
        // this.handleClearBoard = this.handleClearBoard.bind(this);
      }

    componentDidMount() {
        this.generateNewArray();
    }

    onChange(e) {
        const newVal = e.target.value;
        this.setState({slider: newVal}, () => {this.generateNewArray();});
    }

    getRandomInt() {
        const min = 30;
        const max = 500;
        return Math.floor(Math.random() * (max - min + 1)) + min;
    }

    generateNewArray() {
        var array = [];
        for (var i = 0; i < this.state.slider; i++) {
            var height = this.getRandomInt();
            array.push(<GridElement key={i} element_height={height}/>)
        }

        this.setState({array}, () => {console.log(this.state.array);});

    }

    sortElements() {
        var [sorted_array, animations] = myBubbleSort(this.state.array);
        this.setState({array: sorted_array});
    }

    render () {
        return (
            <div className="App">
                <header className="App-header">
                    <span className="header-title">
                        Sorting Visualizer
                    </span>
                    <span className="header-element-size">
                        <div> Size</div>
                        <div> <RangeStepInput 
                        min={5} max={100}
                        value={this.slider} step={1}
                        onChange={this.onChange.bind(this)}
                        /> </div>
                    </span>
                    <span className="header-element">
                        <button className="sort-button" onClick={this.sortElements.bind(this)}> Sort! </button>
                    </span>
                    <span className="header-element">
                        Algorithm
                    </span>
                    <span className="header-element">
                    <button className="sort-button" onClick={this.generateNewArray.bind(this)}> New Array </button>
                    </span>
                </header>
                <div className="sorting-grid">
                    {this.state.array}
                </div>
            </div>

        );
    }
}

export default SortingVisualizer;
