function swap(items, leftIndex, rightIndex){
    var temp = items[leftIndex];
    items[leftIndex] = items[rightIndex];
    items[rightIndex] = temp;
}
function partitionHoare(items, left, right) {
    var animations = [];
    var pivot = items[Math.floor((right + left) / 2)].props.value, //middle element
    i = left, //left pointer
    j = right; //right pointer
    while (i <= j) {
        while (items[i].props.value < pivot) {
            i++;
        }
        while (items[j].props.value > pivot) {
            j--;
        }
        if (i <= j) {
            swap(items, i, j); //sawpping two elements
            animations.push(["swap", i, j]);
            i++;
            j--;
        }
    }
    console.log(animations);
    return [i, animations];
}

function quickSortHoare(items, left, right) {
    var index;
    var _;
    var animationsQuickSort = []
    var animationsLeft = [];
    var animationsRight = [];
    var animationsPartition = [];
    if (items.length > 1) {
        [index, animationsPartition] = partitionHoare(items, left, right); //index returned from partition
        if (left < index - 1) { //more elements on the left side of the pivot
            [_, animationsLeft] = quickSortHoare(items, left, index - 1);
        }
        if (index < right) { //more elements on the right side of the pivot
            [_, animationsRight] = quickSortHoare(items, index, right);
        }
    }
    animationsQuickSort = [...animationsPartition, ...animationsRight, ...animationsLeft];

    return [items, animationsQuickSort];
}

export default quickSortHoare;
