export default function getBubbleSortAnimations(array) {
    // Animations array stores a list of animations for the visualizer
    var animations = [];

    // Bubble sort algorithm
    for (let i = 0; i < array.length; i++) {
        var swapped = false;
        for (let j = 0; j < array.length - i - 1; j++) {
            // Selection animation
            animations.push(["check", j, j+1]);
            var heightOne = array[j].props.element_height;
            var heightTwo = array[j+1].props.element_height;
            if ( heightOne > heightTwo ) {
                // Incorrect location animation
                animations.push(["swap-wrong", j, j+1]);
                // Swap elements animation
                animations.push(["swap-right", j, j+1]);
                var temp = array[j]
                array[j] = array[j+1];
                array[j+1] = temp;
                swapped = true;
            }
            // Return back to origional color animation
            animations.push(["uncheck", j, j+1]);
        }

        if (!swapped) {
            break;
        }

    }
    return animations;
}
