function myBubbleSort(array) {
    var animations = [];
    for (let i = 0; i < array.length; i++) {
        for (let j = 0; j < array.length - i - 1; j++) {
            animations.push([j, j+1]);
            animations.push([j, j+1]);
            if( array[j].props.element_height > array[j + 1].props.element_height ) {
                // animations.push([j, j+1]);
                var temp = array[j]
                array[j] = array[j+1];
                array[j+1] = temp;
            }
        }
    }
    return [array, animations];
}


