import './SortingGrid.css';
import React from 'react';
import GridElement from './GridElement'

function getRandomInt() {
    const min = 25;
    const max = 300;
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

function bubbleSort(arr){
    var i, j;
    var len = arr.length;

    var isSwapped = false;

    for(i =0; i < len; i++){

      isSwapped = false;

      for(j = 0; j < len; j++){ 
          if (arr[j+1] === undefined) {
              continue;
          }
          if(arr[j].props.element_height > arr[j + 1].props.element_height){
            var temp = arr[j]
            arr[j] = arr[j+1];
            arr[j+1] = temp;
            isSwapped = true;
          }
      }

      // IF no two elements were swapped by inner loop, then break 

      if(!isSwapped){
        break;
      }
    }

    // Print the array
    return(arr);
}


function SortingGrid({numElements}) {
    var array = [];
    for (var i = 0; i < 20; i++) {
        var height = getRandomInt();
        array.push(<GridElement key={i} element_height={height}/>)
    }
    var sorted_array = bubbleSort(array);
    // var sorted_array = array;

    return <div className="sorting-grid">
            {sorted_array}
           </div>
}

export default SortingGrid;
