import './sortingVisualizer.css';
import {RangeStepInput} from 'react-range-step-input';
import React from 'react';
import GridElement from './GridElement';

import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { makeStyles } from "@material-ui/core/styles";


import getBubbleSortAnimations from './sorting_algorithms/bubble_sort';
import getBogoSortAnimations from './sorting_algorithms/bogo_sort';

import './App.css';

const dropdownStyles = makeStyles({
    root: {
      boxShadow: "none",
      border: "0px solid rgba(0,0,0,0)",
      "&::before": {
        borderBottom: "0 solid rgba(0,0,0,0)"
      },

      "&:hover": {
        borderBottom: "0 solid rgba(0,0,0,0)"
      },
      "&::after": {
        borderBottom: "0 solid rgba(0,0,0,0)"
      }
    }
});

class SortingVisualizer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          array: [],
          starting_array: [],
          slider: 50,
          algorithm: 'Bubble Sort',
        }
      }

    componentDidMount() {
        this.generateNewArray();
    }

    onChange(e) {
        const newVal = e.target.value;
        this.setState({slider: newVal}, () => {this.generateNewArray();});
    }

    getRandomInt() {
        const min = 30;
        const max = 500;
        return Math.floor(Math.random() * (max - min + 1)) + min;
    }

    generateNewArray() {
        var new_array = [];
        for (var i = 0; i < this.state.slider; i++) {
            var height = this.getRandomInt();
            new_array.push(<GridElement className="grid-element" key={i} element_height={height} style={{backgroundColor: "#282c34"}} />);
        }
        this.setState({array: new_array});
        this.setState({starting_array: new_array});

    }

    printHeights(array_print) {
        var num_array = [];
        for(let i = 0; i < array_print.length; i++) {
            num_array.push(array_print[i].props.element_height);
        }
        console.log(num_array);
    }

    getElementHeight(element) {
        return element.props.element_height;
    }


    sortElements() {
        var animations = getBubbleSortAnimations(this.state.array.slice());
        var temp_array = this.state.array.slice();
        for (let i = 0; i < animations.length; i++) {
            const gridElements = document.getElementsByClassName('grid-element');
            setTimeout(() => {
                const [animation_type, barOneIdx, barTwoIdx] = animations[i];
                const barOne = temp_array[barOneIdx];
                const barTwo = temp_array[barOneIdx];
                if (animation_type === "check") {
                    gridElements[barOneIdx].style.backgroundColor = "green";
                    gridElements[barTwoIdx].style.backgroundColor = "green";
                    // barOne.props.style.backgroundColor = "#ffffff";
                    // barTwo.props.style.backgroundColor = "#ffffff";
                    // console.log(barOne);
                }

                if (animation_type === "uncheck") {

                    gridElements[barOneIdx].style.backgroundColor = "#282c34";
                    gridElements[barTwoIdx].style.backgroundColor = "#282c34";
                }


                if (animation_type === "swap-wrong") {
                    gridElements[barOneIdx].style.backgroundColor = "red";
                    gridElements[barTwoIdx].style.backgroundColor = "red";
                }

                if (animation_type === "swap-right") {
                    var temp = temp_array[barOneIdx]
                    temp_array[barOneIdx] = temp_array[barTwoIdx];
                    temp_array[barTwoIdx] = temp;
                }
                this.setState({array: temp_array});
            }, i * 8);
        }
    }

    updateAlgorithm = (event) => {
        this.setState({algorithm: event.target.value});
      };

    render () {
        // const dropDownStyles = dropdownStyles();
        return (
            <div className="App">
                <header className="App-header">
                    <span className="header-title">
                        Sorting Visualizer
                    </span>
                    <div className="sliders">
                        <span className="slider-div">
                            <div> Size</div>
                            <div> <RangeStepInput 
                            min={5} max={100}
                            value={this.slider} step={1}
                            onChange={this.onChange.bind(this)}
                            /> </div>
                        </span>
                        <span className="slider-div">
                            {/* <div> Speed</div> */}
                            <div> <RangeStepInput 
                            min={5} max={100}
                            value={this.slider} step={1}
                            onChange={this.onChange.bind(this)}
                            /> </div>
                        </span>
                    </div>
                    <span className="header-element">
                        <button className="sort-button" onClick={this.sortElements.bind(this)}> Sort! </button>
                    </span>
                    <span className="header-element">
                    <FormControl variant="standard" className="algorithm-select">
                        <InputLabel variant="standard" sx={{ m: 1, minWidth: 150 }}></InputLabel>
                        <Select
                            labelId="demo-simple-select-label"
                            id="demo-simple-select"
                            value={this.state.algorithm}
                            onChange={this.updateAlgorithm}
                            label="Age"
                            // classes={{
                            //     root: theme.whiteColor,
                            //     icon: classes.whiteColor
                            //   }} 
                            disableUnderline
                        >
                        <MenuItem value={"Bubble Sort"}>Bubble Sort</MenuItem>
                        <MenuItem value={"Quick Sort"}>Quick Sort</MenuItem>
                        <MenuItem value={"Heap Sort"}>Heap Sort</MenuItem>
                        </Select>
                    </FormControl>
                    </span>
                    <span className="header-element">
                    <button className="sort-button" onClick={this.generateNewArray.bind(this)}> New Array </button>
                    </span>
                </header>
                <div className="sorting-grid">
                    {this.state.array}
                </div>
            </div>

        );
    }
}

export default SortingVisualizer;
