import SortingGrid from './SortingGrid'
import {RangeStepInput} from 'react-range-step-input';
import React from 'react';
import GridElement from './GridElement';

import './App.css';

class SortingVisualizer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          array: [],
          slider: 50,
        }
        // this.handleMouseUpdate = this.handleMouseUpdate.bind(this);
        // this.handleClearBoard = this.handleClearBoard.bind(this);
      }

    componentDidMount() {
        this.generateNewArray();
    }

    onChange(e) {
        const newVal = e.target.value;
        this.setState({slider: newVal});
      }

    getRandomInt() {
        const min = 30;
        const max = 500;
        return Math.floor(Math.random() * (max - min + 1)) + min;
    }

    generateNewArray() {
        var newArray = [];
        for (var i = 0; i < 100; i++) {
            var height = this.getRandomInt();
            newArray.push(<GridElement key={i} element_height={height}/>)
        }
        console.log(newArray);
        this.setState({array: newArray})
        console.log(this.state.array);
    }

    render () {
        // console.log(this.array);
        return (
            <div className="App">
                <header className="App-header">
                    <span className="header-title">
                        Sorting Visualizer
                    </span>
                    <span className="header-element-size">
                        <div> Size</div>
                        <div> <RangeStepInput 
                        min={5} max={100}
                        value={this.slider} step={1}
                        onChange={this.onChange.bind(this)}
                        /> </div>
                    </span>
                    <span className="header-element">
                        <button className="sort-button"> Sort! </button>
                    </span>
                    <span className="header-element">
                        Algorithm
                    </span>
                    <span className="header-element">
                    </span>
                </header>
                <div className="sorting-grid">
                    {/* {this.array} */}
                    TEST
                    {/* <SortingGrid className="sorting-grid" numElements={this.slider}/> */}
                </div>
            </div>

        );
    }
 }

export default SortingVisualizer;
