/*
Bubble Sort Description:

Iterate over the array comparing two elements that are next to each other. If the first is greater than the second, swap the two elements and continue.
After that iteration, the largest element will be at the end of the array. Iterate over the array again (not needing to check the last spot) and this 
will put the second largest in the second spot. Continue this process until 1. There are no swaps performed in an iteration (sorted array) or 
2. The final iteration comparing the first two elements occures.  

*/
export default function bubbleSort(array) {
  // Animations array stores a list of animations for the visualizer
  var animations = []; // Bubble sort algorithm

  for (let i = 0; i < array.length; i++) {
    var swapped = false;

    for (let j = 0; j < array.length - i - 1; j++) {
      // Selection animation
      // animations.push(["check", j, j+1]);
      // Get the heights (values) of the elements
      var heightOne = array[j].props.value;
      var heightTwo = array[j + 1].props.value;

      if (heightOne > heightTwo) {
        // Incorrect location animation
        // animations.push(["swap-wrong", j, j+1]);
        // Swap elements animation
        var temp = array[j];
        array[j] = array[j + 1];
        array[j + 1] = temp;
        swapped = true;
        animations.push(["swap", j, j + 1]); // animations.push(["check", j, j+1]);
      } // Return back to origional color animation
      // animations.push(["uncheck", j, j+1]);

    }

    if (!swapped) {
      break;
    }
  }

  return animations;
}

