import SortingGrid from './SortingGrid'
import {RangeStepInput} from 'react-range-step-input';
import React, {useState} from 'react';
import './App.css';

function App() {

  const[slider, setSlider] = useState(50);

  function onChange(e) {
    const newVal = e.target.value;
    setSlider(newVal);
  }
  return (
    <div className="App">
      <header className="App-header">
        <span className="header-title">
          Sorting Visualizer
        </span>
        <span className="header-element-size">
          <div> Size</div>
          <div> <RangeStepInput 
            min={0} max={100}
            value={slider} step={1}
            onChange={onChange.bind(this)}
          /> </div>
        </span>
        <span className="header-element">
          <button className="sort-button"> Sort! </button>
        </span>
        <span className="header-element">
          Algorithm
        </span>
        <span className="header-element">
        </span>
      </header>
      <div>
        <SortingGrid className="sorting-grid"/>
      </div>
    </div>
  );
}

export default App;
