class SortingVisualizer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          array: [],
          slider: 50,
        }
        // this.generateNewArray = this.generateNewArray.bind(this);
        // this.handleClearBoard = this.handleClearBoard.bind(this);
      }

    componentDidMount() {
        this.generateNewArray();
    }

    onChange(e) {
        const newVal = e.target.value;
        this.setState({slider: newVal}, () => {this.generateNewArray();});
    }

    getRandomInt() {
        const min = 30;
        const max = 500;
        return Math.floor(Math.random() * (max - min + 1)) + min;
    }

    generateNewArray() {
        var array = [];
        for (var i = 0; i < this.state.slider; i++) {
            var height = this.getRandomInt();
            array.push(<GridElement key={i} element_height={height}/>)
        }
        this.setState({array});
        // this.setState({array}, () => {console.log(this.state.array);});

    }

    printHeights(array) {
        var num_array = [];
        for(let i = 0; i < array.length; i++) {
            num_array.push(array[i].props.element_height);
        }
        console.log(num_array);
    }

    myBubbleSort(array) {
        console.log("Start of BUBBLE SORT", this.state.array);
        const starting_array = array;
        var animations = [];
        for (let i = 0; i < starting_array.length; i++) {
            for (let j = 0; j < starting_array.length - i - 1; j++) {
                // animations.push([j, j+1]);
                // animations.push([j, j+1]);
                if( starting_array[j].props.element_height > starting_array[j + 1].props.element_height ) {
                    animations.push([j, j+1]);
                    var temp = starting_array[j]
                    starting_array[j] = starting_array[j+1];
                    starting_array[j+1] = temp;
                }
            }
        }
        console.log("End of BUBBLE SORT", this.state.array);
        this.printHeights(this.state.array);
        return [array, animations];
    }


    sortElements() {
        var new_array = this.state.array;
        console.log("STARTING ARRAY: ", this.printHeights(new_array));
        var [starting_array, animations] = this.myBubbleSort(new_array);
        // console.log(animations);
        var temp_array = starting_array;
        console.log("STARTING ARRAY: ", this.printHeights(temp_array));
        console.log("STARTING ARRAY: ", this.printHeights(starting_array));
        console.log("STARTING ARRAY: ", this.printHeights(this.state.array));
        console.log("STARTING ARRAY: ", this.printHeights(new_array));
        // console.log(temp_array);
        for (let i = 0; i < animations.length; i++) {
            // const barOne = this.state.array[barOneIdx];
            // const barTwo = this.state.array[barTwoIdx];
            // console.log(barOne, barTwo);
            // const barOneStyle = barTwo.style;
            // const barTwoStyle = barTwo.style;
            // console.log(barOneStyle, barTwoStyle);
            // return;
            setTimeout(() => {
                const [barOneIdx, barTwoIdx] = animations[i];
                var temp = temp_array[barOneIdx]
                temp_array[barOneIdx] = temp_array[barTwoIdx];
                temp_array[barTwoIdx] = temp;
                // console.log("Swapping:", barOneIdx, barTwoIdx); 

                // this.setState({array: array})
                // this.setState({array: temp_array}, () => {this.printHeights(this.state.array)});
                this.setState({array: temp_array});
            }, i * 300);
        }
        // this.printHeights(sorted_array);
        //     const arrayBars = document.getElementsByClassName('array-bar');
        //     const isColorChange = i % 3 !== 2;
        //     if (isColorChange) {
        //       const [barOneIdx, barTwoIdx] = animations[i];
        //       const barOneStyle = arrayBars[barOneIdx].style;
        //       const barTwoStyle = arrayBars[barTwoIdx].style;
        //       const color = i % 3 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
        //       setTimeout(() => {
        //         barOneStyle.backgroundColor = color;
        //         barTwoStyle.backgroundColor = color;
        //       }, i * 3);
        //     } else {
        //       setTimeout(() => {
        //         const [barOneIdx, newHeight] = animations[i];
        //         const barOneStyle = arrayBars[barOneIdx].style;
        //         barOneStyle.height = `${newHeight}px`;
        //       }, i * 3);
        //     }
        //   }
        console.log("TEST");
        // this.setState({array: starting_array});
        this.setState({array: starting_array}, () => {console.log(this.state.array)});
        // console.log("Final heights");
        // this.printHeights(this.state.array);
        // this.setState({array: sorted_array}, () => {this.printHeights(this.state.array)});
        // this.printHeights()
        // console.log(animations)
        // console.log(this.state.array);
    }

    render () {
        return (
            <div className="App">
                <header className="App-header">
                    <span className="header-title">
                        Sorting Visualizer
                    </span>
                    <span className="header-element-size">
                        <div> Size</div>
                        <div> <RangeStepInput 
                        min={5} max={100}
                        value={this.slider} step={1}
                        onChange={this.onChange.bind(this)}
                        /> </div>
                    </span>
                    <span className="header-element">
                        <button className="sort-button" onClick={this.sortElements.bind(this)}> Sort! </button>
                    </span>
                    <span className="header-element">
                        Algorithm
                    </span>
                    <span className="header-element">
                    <button className="sort-button" onClick={this.generateNewArray.bind(this)}> New Array </button>
                    </span>
                </header>
                <div className="sorting-grid">
                    {this.state.array}
                </div>
            </div>

        );
    }
}

export default SortingVisualizer;
