import React from 'react';
import './header.css'

import InputLabel from '@mui/material/InputLabel';
import TextField from "@material-ui/core/TextField";
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { makeStyles } from "@material-ui/core/styles";
import {RangeStepInput} from 'react-range-step-input';

// Function to update the size property

const styles = makeStyles({
    root: {
        width: 200,
        "& .MuiOutlinedInput-input": {
          color: "white"
        },
        "& .MuiInputLabel-root": {
          color: "white"
        },
        "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
          borderColor: "green"
        },
        "&:hover .MuiOutlinedInput-input": {
          color: "red"
        },
        "&:hover .MuiInputLabel-root": {
          color: "red"
        },
        "&:hover .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
          borderColor: "red"
        },
        "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-input": {
          color: "purple"
        },
        "& .MuiInputLabel-root.Mui-focused": {
          color: "purple"
        },
        "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline": {
          borderColor: "purple"
        }
    },
    whiteColor: {
      color: "white"
    }
  });

function Header(props) {
    const classes = styles();
    return(
        <header className="App-header">
        <span className="header-title">
            Sorting Visualizer
        </span>
        <div className="sliders">
            <span className="slider-div">
                <div className="slider-title"> Size</div>
                <div> <RangeStepInput 
                min={4} max={50}
                value={props.size} step={1}
                onChange={props.onSizeChange.bind(this)}
                /> </div>
            </span>
            <span className="slider-div">
                <div className="slider-title"> Speed</div>
                <div> <RangeStepInput 
                min={5} max={500}
                value={props.speed} step={1}
                onChange={props.onSpeedChange.bind(this)}
                /> </div>
            </span>
        </div>
        <div className="header-buttons">
            <div className="header-button">
                <button className="sort-button" onClick={props.generateNewArray}> New Array </button>
            </div>
            <div className="header-button"> 
                <button className="sort-button" onClick={props.sortElements}> Sort! </button>
            </div>
        </div>
        <span className="header-element">
        {/* <FormControl variant="standard" className="algorithm-select"> */}
            {/* <InputLabel variant="standard" sx={{ m: 1, minWidth: 150 }}></InputLabel> */}
            <TextField
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={props.algorithm}
                onChange={props.updateSelectedAlgorithm}
                label="Age"
                className={classes.root}
                variant="outlined"
                select
                >
            <MenuItem value={"Bubble Sort"}>Bubble Sort</MenuItem>
            <MenuItem value={"Quick Sort"}>Quick Sort</MenuItem>
            <MenuItem value={"Heap Sort"}>Heap Sort</MenuItem>
            </TextField>
        {/* </FormControl> */}
        </span>
        </header>
 );

}

export default Header;
